mapper.select:
SELECT c.claim_no, status_code, type_code, request_code, cause_code, customer_blame, store_blame, partner_blame, mesh_blame, none_blame, creator, create_dt, updater, update_dt, order_id, claim_order_id, order_number, claim_order_number FROM ( SELECT claim_no, status_code, type_code, request_code, cause_code, customer_blame, store_blame, partner_blame, mesh_blame, none_blame, creator, create_dt, updater, update_dt FROM claim WHERE 1 = 1
AND type_code = #{claimType}
AND request_code = #{requestType}
AND status_code IN
#{item}
AND claim_no LIKE CONCAT('%', #{searchString}, '%')
AND create_dt BETWEEN #{request.startDate} AND #{request.endDate} ) c INNER JOIN ( SELECT order_id, claim_no, claim_order_id, order_number, claim_order_number FROM order_claim_relation WHERE 1 = 1
AND order_id IN
#{item}
AND order_number LIKE CONCAT('%', #{searchString}, '%')
AND claim_order_number LIKE CONCAT('%', #{searchString}, '%')
AND claim_no LIKE CONCAT('%', #{searchString}, '%')
) o ON c.claim_no = o.claim_no ORDER BY claim_no DESC

mapper.update:
UPDATE order_claim_relation SET
	claim_order_id = #{claimOrderId},
	claim_order_number = #{claimOrderNumber}
  WHERE claim_no = #{claimInfo.claimNo}
